/**
 * Definition for singly-linked list node
 */
class ListNode {
  constructor(val = 0, next = null) {
    this.val = val;
    this.next = next;
  }
}

/**
 * Reverses a singly linked list
 * Time Complexity: O(n)
 * Space Complexity: O(1)
 * 
 * @param {ListNode} head - Head of the linked list
 * @return {ListNode} - Head of the reversed linked list
 */
function reverseLinkedList(head) {
  let prev = null;
  let current = head;

  while (current !== null) {
    // Save next node
    const next = current.next;

    // Reverse pointer
    current.next = prev;

    // Move pointers one step forward
    prev = current;
    current = next;
  }

  // prev is the new head
  return prev;
}

// Example usage:
// const list = new ListNode(1, new ListNode(2, new ListNode(3, new ListNode(4))));
// const reversed = reverseLinkedList(list);
// Output: 4 -> 3 -> 2 -> 1 -> null

/**
 * Definition for a binary tree node
 */
class TreeNode {
  constructor(val = 0, left = null, right = null) {
    this.val = val;
    this.left = left;
    this.right = right;
  }
}

/**
 * Determines if a binary tree is height-balanced.
 * A height-balanced tree is a tree in which the left and right subtrees 
 * of every node differ in height by no more than 1.
 * 
 * Time Complexity: O(n) where n is the number of nodes in the tree
 * Space Complexity: O(h) where h is the height of the tree (due to recursion stack)
 * 
 * @param {TreeNode} root - Root of the binary tree
 * @return {boolean} - Whether the tree is balanced or not
 */
function isBalanced(root) {
  // Function returns -1 if the tree is unbalanced, otherwise returns the height
  function checkHeight(node) {
    if (node === null) return 0;

    const leftHeight = checkHeight(node.left);
    if (leftHeight === -1) return -1; // Left subtree is unbalanced

    const rightHeight = checkHeight(node.right);
    if (rightHeight === -1) return -1; // Right subtree is unbalanced

    // Check if current node is balanced
    if (Math.abs(leftHeight - rightHeight) > 1) return -1;

    // Return height of current subtree
    return Math.max(leftHeight, rightHeight) + 1;
  }

  return checkHeight(root) !== -1;
}

