    }

    // Follows SRP and has high cohesion
    public class ReportGenerator
    {
        public string Generate() { /* ... */ }
    }

    public class ReportSaver
    {
        public void Save(string report) { /* ... */ }
    }

    public class EmailSender
    {
        public void Send(string report) { /* ... */ }
    }
